INSERT INTO `wisatas` (`id`, `nama_wisata`, `alamat_wisata`, `kecamatan_wisata`, `daerah_wisata`, `deskripsi_tempat`, `biaya_tiket`, `kategori`, `longitude`, `latitude`, `gambar_wisata`, `created_at`, `updated_at`, `fasilitas`, `link_traveloka`, `pengunjung`) VALUES
(11, 'Kadadima Waterfall', 'Kampung Laine', 'Manganitu Selatan', 6, '<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xdj266r x126k92a\">\r\n<div dir=\"auto\"><span class=\"x3nfvp2 x1j61x8r x1fcty0u xdj266r xhhsvwb xat24cr xgzva0m xxymvpz xlup9mm x1kky2od\"><img src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t2d/1/16/1f4cd.png\" alt=\"\" width=\"16\" height=\"16\"></span> <strong>Air Terjun Kadadima</strong></div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Terletak di Desa Laine, Kecamatan Manganitu Selatan, Kabupaten Kepulauan Sangihe , Propinsi Sulawesi Utara.</div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Aksesbilitas</div>\r\n<div dir=\"auto\">Berjarak tempuh sekitar 90 menit dengan kendaraan darat dari Tahuna atau 25 menit dari Pelabuhan feri Pananaru. Sedangkan dari Desa Laine sekitar 25 menit ke arah timur dengan cara berjalan kaki.</div>\r\n<div dir=\"auto\">Air terjun Kadadima ini unik. Karena merupakan nama pengganti dari 3 air terjun yang ada.</div>\r\n<div dir=\"auto\">Keunikan air terjun ini karena terdapat 3 air terjun yang bernama Nahepese, Elong dan Matei yang masih merupakan satu aliran sungai yang sama.</div>\r\n</div>', 0, 1, '3.446978206438392', '125.6000463975709', '1679657521389.air terjun laine.jpeg', '2023-03-24 04:32:01', '2023-05-28 19:55:57', 'Tempat Parkir', NULL, 5),
(12, 'Ngurahlawo Waterfall', 'Kampung Lelipang', 'Tamako', 8, '<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xdj266r x126k92a\">\r\n<div dir=\"auto\"><span class=\"x3nfvp2 x1j61x8r x1fcty0u xdj266r xhhsvwb xat24cr xgzva0m xxymvpz xlup9mm x1kky2od\"><img src=\"https://static.xx.fbcdn.net/images/emoji.php/v9/t2d/1/16/1f4cd.png\" alt=\"\" width=\"16\" height=\"16\"></span> <strong>Air Terjun Ngurahlawo</strong></div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Kecamatan Tamako terkenal dengan banyak destinasi wisata yang mempesona,salah satunya AIR TERJUN NGURAHLAWO.</div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Air terjun ini terletak di kampung Lelipang kecamatan Tamako dapat dengan mudah di jangkau.Dari kota Tahuna hanya membutuhkan waktu sekitar 35 menit jika menggunakan sepeda motor untuk sampai di kampung Lelipang.Selanjutnya pengunjung akan menyusuri jalan perkebunan sekitar 20 menit,tidak perlu kuatir karena jalan ini sudah bagus dan selama perjalanan pengunjung bisa menikmati alam hutan tropis sangihe.Jikalau beruntung anda bisa melihat burung burung endemik sangihe yang masih bebas hidup di hutan ini.</div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Air terjun Lelipang menyuguhkan 2 Air terjun yang berdekatan yang memiliki panorama alam indah,cocok untuk yang hobi petualang dan fotografi</div>\r\n</div>', 0, 1, '3.4851780746603764', '125.54026049416514', '1679657750408.cr paradise of sangihe.jpg', '2023-03-24 04:35:50', '2023-03-24 20:53:28', 'Tempat Parkir', NULL, 1),
(13, 'Pempanikiang Waterfall', 'Kampung Kendahe I', 'Kendahe', 17, '<p>Air Terjun Pempanikiang<br>Saat berkunjung ke air terjun Pempankiang seperti berada dalam batu raksasa karena dikelilingi oleh tebing batu dengan kemiringan hampir &nbsp;90 derajat. Airnya yang jernih dengan lokasi yang eksotik membuat kita betah beralama-lama di tempat ini.<br>30 Menit dari kota Tahuna di lanjutkan dengan menyusuri anak sungai sekitar 15 menit kita akan di suguhkan dengan panorama alam hutan tropis yang memanjakan mata.<br>Kalian semua wajib kesini teman <br>Jangan lupa untuk selalu menjaga kebersihan.Salah satunya dengan tidak membuang sampah plastik sembarangan &nbsp;</p>', 0, 1, '3.70340445306821', '125.41389405162789', '1679657892733.cr pos 2.jpg', '2023-03-24 04:38:12', '2023-03-24 20:55:00', 'Tempat Parkir', NULL, 1),
(14, 'Sura Kakewang Waterfall', 'Kampung Kendahe II', 'Kendahe', 17, '<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xdj266r x126k92a\">\r\n<div dir=\"auto\"><strong>Air Terjun Sura Kakewang</strong></div>\r\n<div dir=\"auto\">&nbsp;</div>\r\n<div dir=\"auto\">Di tengah lebatnya hutan dan muara, sungai kakewang kecamatan kendahe menyimpan panorama dan kesejukan air terjun dengan tinggi kira-kira 20 meter sebuah sajian alam menakjubkan. Air terjun ini merupakan bagian dari aliran muara sungai yang berhulu langsung dari gunung api awu sangihe jadi tidak heran memiliki kemurnian dan kesejukan alam masih terlalu alami untuk di nikmati.</div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Akses yang cukup menantang dan perlu kehati-hatian untuk sampai ke spot ini kiranya menjadi tantangan tersendiri yah, apalagi bagi para petualang penikmat alam bebas pasti selalu bisa menaklukkan bagian tampak sangihe yang keren ini.</div>\r\n<div dir=\"auto\">&nbsp;</div>\r\n<div dir=\"auto\"><span class=\"x3nfvp2 x1j61x8r x1fcty0u xdj266r xhhsvwb xat24cr xgzva0m xxymvpz xlup9mm x1kky2od\">Sumber Gambar :</span></div>\r\n<div dir=\"auto\"><span class=\"x3nfvp2 x1j61x8r x1fcty0u xdj266r xhhsvwb xat24cr xgzva0m xxymvpz xlup9mm x1kky2od\">1. Nessa Masihor</span></div>\r\n<div dir=\"auto\"><span class=\"x3nfvp2 x1j61x8r x1fcty0u xdj266r xhhsvwb xat24cr xgzva0m xxymvpz xlup9mm x1kky2od\">2. Laminding Tour Sangihe</span></div>\r\n</div>', 0, 1, '3.682027271635799', '125.42730793728295', '1679658255261.1.jpg', '2023-03-24 04:44:15', '2023-03-24 20:56:08', 'Tempat Parkir', NULL, 1),
(15, 'Pensu Waterfall', 'Kelurahan Pananekeng', 'Tahuna Timur', 13, '<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xdj266r x126k92a\">\r\n<div dir=\"auto\"><strong>Air terjun Pensu, Pananekeng</strong></div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Jalur pendakian gunung awu sangihe pananekeng memiliki kelebihan tersendiri. Sebab air terjun ini berada pada jalur lintasan tersebut yang terletak di wilayah pensu pananekeng, kecamatan Tahuna barat. Memiliki pemandangan yang mengagumkan, air yang jernih ini mengalir sangat deras memiliki wadah atau kolam mini terletak di hutan yang masih asri sehingga atmosfir udara terasa sangat segar.</div>\r\n</div>\r\n<div class=\"x11i5rnm xat24cr x1mh8g0r x1vvkbs xtlvy1s x126k92a\">\r\n<div dir=\"auto\">Akses jalan yang tidak jauh dari jalan utama wilayah pananekeng dan mudah di jangkau, spot yang satu ini sangat layak untuk di kelola, dan di kemas untuk menambah rekomendasi baru lokasi pariwisata di kepulauan Sangihe.</div>\r\n<div dir=\"auto\">&nbsp;</div>\r\n<div dir=\"auto\">Sumber Gambar :</div>\r\n<div dir=\"auto\">1. Everson Tatawi Pontolawokang</div>\r\n<div dir=\"auto\">2. Thian Ekon</div>\r\n</div>', 0, 1, '3.6316197149612095', '125.46138561509713', '1679658648518.cr everson tatawi pontolawokang.jpg', '2023-03-24 04:50:48', '2023-03-24 20:55:36', 'Tempat Parkir', NULL, 1),
(16, 'Batu Senggelohe', 'Kampung Lebo', 'Manganitu', 5, '<p><strong>Batu Senggelohe, Lebo</strong></p>\r\n<p>Batu Senggelohe ini merupakan salah satu batu yang berada di tengah laut, yang berada di Kampung Lebo, Kecamatan Manganitu. Para wisatawan bisa menempuh kurang lebih 45 menit dari Pusat Kota Tahuna untuk sampai di tempat ini. Untuk menyebrang ke Batu Senggelohe ini, bisa dengan menyewa perahu yang ada di pinggir pantai Pendarehokang - Salenggihing, ataupun Berjalan Kaki pada saat Air Surut.</p>\r\n<p>&nbsp;</p>\r\n<p>Sumber Gambar ;</p>\r\n<ol>\r\n<li>Maydelin Kondoahi</li>\r\n<li>Priscilia Tumewu</li>\r\n<li>Steven Paparang</li>\r\n<li>Yohanis Sahabat</li>\r\n</ol>', 10000, 2, '3.5023957328063022', '125.48567491604787', '1679659040352.cr priscillia tumewu.jpg', '2023-03-24 04:57:20', '2023-03-24 04:57:20', 'Tempat Parkir', NULL, 1),
(17, 'Batuwingkung Island', 'Kampung Batuwingkung', 'Tabukan Selatan', 9, '<p>Batuwingkung merupakan salah satu desa yang berada di kecamatan Tabukan Selatan, Kabupaten Kepulauan Sangihe, provinsi Sulawesi Utara, Indonesia</p>\r\n<p>Sumber gambar :</p>\r\n<ol>\r\n<li>Christy</li>\r\n<li>Arsip DISPARDA Sangihe</li>\r\n</ol>', 100000, 2, '3.5390097422417566', '125.65720182329676', '1679659261382.disparda.jpg', '2023-03-24 05:01:01', '2023-03-24 05:01:01', 'Toilet, Tempat Parkir', NULL, 1),
(18, 'Barangkalang Underwater', 'Kampung Barangkalang', 'Manganitu', 5, '<p><strong>Barangkalang <em>Underwater</em></strong></p>\r\n<p>Salah satu destinasi wisata wajib yang kamu kunjungi. Disini kamu bisa <em>diving&nbsp;</em>dan dimanjakan oleh pandangan terumbu karang yang cantik beserta ragam biota laut yang ada. Untuk destinasi harganya, sudah termasuk dengan alat snorkling yang bisa kamu dapatkan dengan menghubungi <span style=\"text-decoration: underline;\">Paket Wisata Sangihe</span></p>\r\n<p>&nbsp;</p>\r\n<p>Sumber Gambar :</p>\r\n<p>Jhonlihar (DISPARDA Sangihe)</p>', 100000, 4, '3.478753687777412', '125.49462327134435', '1679662022244.Jhonlihar_-_Barangkalang_-_Disparda_Kab_Sangihe_-_2021-1[1].jpg', '2023-03-24 05:47:02', '2023-03-24 05:47:02', 'Toilet, Tempat Parkir', NULL, 1),
(19, 'Bowong Banua', 'Kampung Batunderang', 'Manganitu Selatan', 6, '<p>Salah satu tempat yang tepat untuk menikmati sunset yang megah adalah \"BOWONG BANUA\" tempat ini begitu populer untuk para pecinta fotografi atau pengunjung yang hanya sekedar selfi bersama teman-teman,<br>Tidak sulit untuk datang ke sini,Berada tepat diantara kampung Sowaeng dan Kampung Batunderang di kecamatan Manganitu Selatan &nbsp;sekitar 90 Menit dari Kota Tahuna menggunakan sepeda motor.<br>Pengunjung selalu diwajibkan melapor atau meminta ijin kepada pemilik lahan maupun pemerintah kampung<br><br>Kalian semua wajib kesini teman.<br>Jangan lupa untuk selalu menjaga kebersihan ya.Salah satunya dengan tidak membuang sampah plastik sembarangan ya teman&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>Sumber Foto :</p>\r\n<ol>\r\n<li>Destrong Parera</li>\r\n<li>Stenly Pontolawokang</li>\r\n<li>Dicky Makapuas</li>\r\n<li>Randi Tentonda</li>\r\n</ol>', 1, 3, '3.3701429495502', '125.61229160018318', '1679662679335.cr randi tentonda.jpg', '2023-03-24 05:57:59', '2023-03-24 05:57:59', 'Tempat Parkir', NULL, 1),
(20, 'Bukide Timur', 'Kampung Bukide Timur', 'Nusa Tabukan', 15, '<p><span lang=\"EN-US\">Kampung <strong>Bukide Timur</strong> terletak disebuah pulau yaitu pulau bukide yang membentang dari arah barat ke timur.</span></p>\r\n<p><span lang=\"EN-US\">Batas wilayah kampung bukide timur :</span></p>\r\n<ul>\r\n<li><span lang=\"EN-US\">Sebelah Utara berbatasan dengan laut Kec. Kendahe</span></li>\r\n<li><span lang=\"EN-US\">Sebelah selatan berbatasan dengan laut kecamatan tabukan selatan</span></li>\r\n<li><span lang=\"EN-US\">Sebelah timur berbatasan dengan laut kecamatan marore</span></li>\r\n<li><span lang=\"EN-US\">Dan sebelah barat berbatasan dengan kampung bukide.</span></li>\r\n</ul>\r\n<p><span lang=\"EN-US\">Kampung Bukide Timur memiliki kuliner yang khas yaitu kue amik dengan peralatan yang unik. Kampung bukide timur mempunyai wisata bahari yaitu pantai pasir putih kasaraeng, dengan panjang pantai 1300meter, bukide batu, pulau buang, dan terumbuh karang yang indah serta mamalia laut yang selalu berada di wilayah pesisir bukide timur, dikampung bukide timur juga terdapat spesies hewan endemik seperti khusus beruang (ursinusmelanoties), burung maleo dan punting (merpati laut/ducula bicolor)</span></p>\r\n<p><span lang=\"EN-US\">Kampung Bukide timur terletak di Kecamatan Nusa Tabukan, Kabupaten Kepulauan Sangihe. untuk menuju ke kampung bukide timur dari Kota Tahuna, ibu kota Sangihe, Pulau Bukide bisa dicapai sekitar dua jam perjalanan dengan perahu bermotor tempel.<br><br>Kawasan perairan Bukide Timur di Kepulauan Sangihe, Sulawesi Utara kaya dengan berbagai sumberdaya ikan, baik yang didominasi oleh ikan deho (sejenis ikan tongkol) dan ikan malalugis (sejenis ikan pelagis kecil bernama latin <em>Decapterus Macarellus</em>).</span></p>\r\n<p><span lang=\"EN-US\" style=\"font-size: 11.0pt; line-height: 107%; font-family: \'Calibri\',sans-serif; mso-ascii-theme-font: minor-latin; mso-fareast-font-family: Calibri; mso-fareast-theme-font: minor-latin; mso-hansi-theme-font: minor-latin; mso-bidi-font-family: \'Times New Roman\'; mso-bidi-theme-font: minor-bidi; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;\">Di pesisir Bukide Timur tinggal warga sebanyak 475 jiwa, yang sekitar 75 persennya menggantungkan mata pencariannya sebagai nelayan. Tak heran, hasil tangkapan laut lalu menjadi harapan hidup masyarakat.<br style=\"mso-special-character: line-break;\"><!-- [if !supportLineBreakNewLine]--><br style=\"mso-special-character: line-break;\"><!--[endif]--></span></p>', 100000, 5, '3.7888492736970303', '125.6196717288571', '1679672162998.116560_small.jpg', '2023-03-24 08:36:02', '2023-03-24 08:36:02', 'Homestay / Penginapan, Toilet', NULL, 1),
(21, 'Gunung Api Bawah Laut Banua Wuhu', 'Kampung Mahengetang', 'Tatoareng', 7, '<p><strong>Banua Wuhu</strong> adalah gunung api bawah laut yang terletak di sebelah barat pulau Mahengetang, Kepulauan Sangihe, Indonesia. Gunung ini memiliki ketinggian 400 meter dari dasar laut dan puncaknya berada di kedalaman 5 meter di bawah permukaan air. Catatan sejarah menunjukkan bahwa beberapa pulau sementara terbentuk dan kemudian menghilang.<br>Jenis gunung : Submarine volcano, lava dome<br>Letusan terakhir : July to December 1919<br>Pulau setinggi 90 meter terbentuk pada tahun 1835, tetapi kemudian menjadi hanya beberapa batu pada tahun 1848. Letusan pada September 1889 membentuk sebuah pulau baru yang mencapai ketinggian 50 meter pada tahun 1894. Letusan pada bulan April dan Agustus 1904 membentuk 5 buah kawah.<br>Pulau baru lainnya terbentuk pada bulan Juli 1918 hingga Desember 1919, dan menghilang sepenuhnya pada tahun 1935. Letusan kembali terjadi pada bulan April 1919, batu-batu besar dan abu melanda pulau Mahengetang. Rumah-rumah banyak yang terbakar sehigga banyak penghuni yang meninggalkan pulau Mahengetang</p>\r\n<p>&nbsp;</p>\r\n<p>Sumber : DISPARDA Sangihe</p>', 300000, 4, '3.143276809131779', '125.49019846694547', '1679673066898.cr moses kalampung.jpg', '2023-03-24 08:51:06', '2023-03-24 08:51:06', 'Homestay / Penginapan, Toilet', NULL, 1),
(22, 'Gunung Awu', 'Kampung Utaurano', 'Tabukan Utara', 14, '<p><strong>Gunung Awu</strong> adalah gunung dengan jenis stratovolcano yang terletak di Kepulauan Sangihe. Memiliki ketinggian 1320 Mdpl. Berdasarkan sejarah, letusan besar gunung awu pernah terjadi pada tahun-tahun 1711, 1812, 1856, 1892 and 1966 yang menyebabkan lebih 8000 orang tewas akibat letusan gunung Awu.</p>\r\n<p>&nbsp;</p>\r\n<p>Sumber gambar :</p>\r\n<ol>\r\n<li>Andy Lahindo Gansalangi</li>\r\n<li>Marco Karwur</li>\r\n<li>Stvnpngky</li>\r\n<li>Dolvi Silangen</li>\r\n</ol>', 1000, 3, '3.6711070654002564', '125.49866713392849', '1679673606320.cr andy gansalangi.jpg', '2023-03-24 09:00:06', '2023-03-24 09:00:06', 'Tempat Parkir', NULL, 1),
(23, 'Desa Wisata Hesang', 'Kampung Hesang', 'Tamako', 8, '<p class=\"MsoNormal\" style=\"mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; line-height: normal;\"><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Desa <strong>Hesang</strong> merupakan salah satu Desa di Kecamatan Tamako, yang berada di bagian Selatan dari Ibu Kota Kabupaten Kepulauan Sangihe. Batas-batas wilayah sebagai berikut :</span></p>\r\n<ul type=\"disc\">\r\n<li class=\"MsoNormal\" style=\"mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; line-height: normal; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt;\"><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Sebelah Utara berbatasan dengan Kampung Pokol</span></li>\r\n<li class=\"MsoNormal\" style=\"mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; line-height: normal; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt;\"><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Sebelah Timur berbatasan dengan Kampung Menggawa 1</span></li>\r\n<li class=\"MsoNormal\" style=\"mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; line-height: normal; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt;\"><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Sebelah Selatan berbatasan dengan Laut Sulawesi</span></li>\r\n<li class=\"MsoNormal\" style=\"mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; line-height: normal; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt;\"><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Sebelah Barat berbatasan dengan Laut Sulawesi</span></li>\r\n</ul>\r\n<p class=\"MsoNormal\" style=\"mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; line-height: normal;\"><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Luas wilayah Desa Hesang adalah &plusmn;400 Ha, dan terdiri atas 3 (tiga) Lindongan. Desa Hesang juga berada pada ketinggian 76 Meter dari permukaan laut, dan secara keseluruhan Desa Hesang merupakan daerah tepi/pesisir pantai. &nbsp;Desa Hesang dicanangkan sebagai Desa Bersinar (Bersih Narkoba) oleh BNN.</span></p>\r\n<p class=\"MsoNormal\" style=\"mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; line-height: normal;\"><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Di Desa Wisata Hesang ini, ada berbagai pilihan wisata, antara lain; Pantai. Hutan Manggrove, Kolam Renang, dsb.</span></p>\r\n<p class=\"MsoNormal\" style=\"mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; line-height: normal;\"><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Sumber Gambar :</span></p>\r\n<ol>\r\n<li><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Arsip DISPARDA Sangihe</span></li>\r\n<li><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Stenly Pontolawokang</span></li>\r\n<li><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Audro Christofel</span></li>\r\n<li><span lang=\"EN-US\" style=\"font-size: 12.0pt; font-family: \'Times New Roman\',serif; mso-fareast-font-family: \'Times New Roman\';\">Fandri Ruminggu</span></li>\r\n</ol>', 10000, 2, '3.44532966698